import React, { Component } from 'react';

class Input extends Component {
    constructor(props){
        super(props);
        this.state = { 
            value1: '',
            value2: '',
            result: '' 
        };
        //this.state = { value2: '' };
        this.handleChange1 = this.handleChange1.bind(this);
        this.handleChange2 = this.handleChange2.bind(this);
    }

    handleChange1(e){
        this.setState({ value1 : e.target.value });
    }

    handleChange2(e){
        this.setState({ value2 : e.target.value });
    }

    addvalues=(e)=> {
        let num1 = this.state.value1;
        let num2 = this.state.value2;

        let res = num1 + num2;

        console.log(res);

        this.setState({result: res });
    }

    render() {
        return (
            <div className="test">
                {this.state.value1}
                <label>
                    Value 1:
                    <input type="text" value={this.state.value1} onChange={this.handleChange1} />
                </label>
                <br></br>
                {this.state.value2}
                <label>
                    Value 2:
                    <input type="text" value={this.state.value2} onChange={this.handleChange2} />
                </label>
                <br></br>
                <label>
                    <input type="button" value="Submit" onSubmit={this.addvalues} />
                    Sum: {this.state.result}</label>
            </div>
        );
    }
}

export default Input;
